   import { bootLayout } from '/assets/js/layout.js'; bootLayout('shop');
   const CART_KEY = "intreen_cart_v1";
   function load(){ try{ return JSON.parse(localStorage.getItem(CART_KEY) || "[]"); }catch(e){ return [] } }
   function money(n){ return `$${Number(n).toFixed(2)}`; }
   const items = load();
   const lines = document.getElementById('order-lines');
   const subtotalEl = document.getElementById('subtotal');
   const shipEl = document.getElementById('shipcost');
   const discEl = document.getElementById('discount');
   const totalEl = document.getElementById('order-total');
   function calc(){
     const sub = items.reduce((t,i)=>t+i.price*i.qty,0);
     const ship = document.querySelector('input[name=ship]:checked').value==='express' ? 14 : 6;
     const promo = (document.getElementById('promo').value.trim().toUpperCase()==='SAVE10') ? 10 : 0;
     subtotalEl.textContent = money(sub);
     shipEl.textContent = money(sub>0?ship:0);
     discEl.textContent = promo?`-${money(promo)}`:`-${money(0)}`;
     totalEl.textContent = money(Math.max(0, sub + (sub>0?ship:0) - promo));
   }
   if(items.length===0){ lines.innerHTML = ' Your cart is empty. '; }
   else{
     lines.innerHTML = items.map(i=>`  ${i.title} × ${i.qty}  $${(i.price*i.qty).toFixed(2)}  `).join('');
   }
   calc();
   document.querySelectorAll('input[name=ship]').forEach(r=>r.addEventListener('change', calc));
   document.getElementById('promo').addEventListener('input', calc);
   document.getElementById('checkout-form').addEventListener('submit', (e)=>{
     e.preventDefault();
     // demo success
     localStorage.setItem(CART_KEY, "[]");
     alert('Order placed successfully! (demo)');
     location.href = '/shop/index.html';
   });

   // LUHN_VALIDATION + simple field checks
   function luhnOk(num){
     num = num.replace(/\s+/g,'');
     if(!/^\d{13,19}$/.test(num)) return false;
     let sum=0, alt=false;
     for(let i=num.length-1;i>=0;i--){
       let n = parseInt(num[i],10);
       if(alt){ n*=2; if(n>9) n-=9; }
       sum+=n; alt=!alt;
     }
     return sum%10===0;
   }
   function expOk(v){
     const m = v.match(/^(0[1-9]|1[0-2])\/(\d{2})$/);
     if(!m) return false;
     const mm = parseInt(m[1],10), yy = 2000 + parseInt(m[2],10);
     const now = new Date(); const exp = new Date(yy, mm);
     return exp > now;
   }
   function cvcOk(v){ return /^\d{3,4}$/.test(v); }
   function emailOk(v){ return /^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(v); }
   document.getElementById('checkout-form').addEventListener('submit', (e)=>{
     const email = document.getElementById('email').value.trim();
     const card = document.getElementById('card').value.trim();
     const exp = document.querySelector('input[placeholder="MM/YY"]').value.trim();
     const cvc = document.querySelector('input[placeholder="123"]').value.trim();
     if(!emailOk(email)){ alert('Please enter a valid email.'); e.preventDefault(); return; }
     if(!luhnOk(card)){ alert('Please enter a valid card number.'); e.preventDefault(); return; }
     if(!expOk(exp)){ alert('Please enter a valid expiry (MM/YY).'); e.preventDefault(); return; }
     if(!cvcOk(cvc)){ alert('Please enter a valid CVC.'); e.preventDefault(); return; }
   });

 // Format card as XXXX XXXX XXXX XXXX and enforce digits only + max len
 const card = document.getElementById('card');
 card.addEventListener('input', ()=>{
   let v = card.value.replace(/\D+/g,'');
   if(v.length>19) v = v.slice(0,19);
   card.value = v.replace(/(.{4})/g,'$1 ').trim();
 });
 const exp = document.getElementById('exp');
 exp.addEventListener('input', ()=>{
   let v = exp.value.replace(/\D+/g,'');
   if(v.length>4) v=v.slice(0,4);
   if(v.length>=3) v = v.slice(0,2)+'/'+v.slice(2);
   exp.value = v;
 });
 const cvc = document.getElementById('cvc');
 cvc.addEventListener('input', ()=>{
   cvc.value = cvc.value.replace(/\D+/g,'').slice(0,3);
 });



