  let today = new Date(); today.setDate(1);
  function render(date){
    mini.innerHTML = '';
    const y=date.getFullYear(), m=date.getMonth();
    const head = ['Sun','Mon','Tue','Wed','Thu','Fri','Sat'];
    head.forEach(h=>{ const e=document.createElement('div'); e.className='day head'; e.textContent=h; mini.appendChild(e); });
    const first = new Date(y,m,1).getDay();
    const days = new Date(y,m+1,0).getDate();
    for(let i=0;i { today.setMonth(today.getMonth()-1); render(today); };
  document.getElementById('cal-next').onclick = ()=>{ today.setMonth(today.getMonth()+1); render(today); };
})();





