 const US_STATES = ["AL","AK","AZ","AR","CA","CO","CT","DE","FL","GA","HI","ID","IL","IN","IA","KS","KY","LA","ME","MD","MA","MI","MN","MS","MO","MT","NE","NV","NH","NJ","NM","NY","NC","ND","OH","OK","OR","PA","RI","SC","SD","TN","TX","UT","VT","VA","WA","WV","WI","WY"];
 const CA_PROVINCES = ["AB","BC","MB","NB","NL","NS","NT","NU","ON","PE","QC","SK","YT"];
 function fillRegions(country){
   const r = document.getElementById('region');
   r.innerHTML='';
   const list = country==='Canada'? CA_PROVINCES : (country==='United States' || country==='US'? US_STATES : []);
   if(list.length){
     list.forEach(x=>{ const o=document.createElement('option'); o.textContent=x; o.value=x; r.appendChild(o); });
   }else{
     const o=document.createElement('option'); o.textContent='N/A'; r.appendChild(o);
   }
 }
 const cSel = document.getElementById('country');
 fillRegions(cSel.value);
 cSel.addEventListener('change', ()=>fillRegions(cSel.value));
